import { useState, useEffect, useRef } from "react";

const sortStatuses = [
  { name: "популярности (по убыв.)", sortName: "rating" },
  { name: "цене (по убыв.)", sortName: "price" },
  { name: "алфавиту (по убыв.)", sortName: "title" },
  { name: "популярности (по возр.)", sortName: "-rating" },
  { name: "цене (по возр.)", sortName: "-price" },
  { name: "алфавиту (по возр.)", sortName: "-title" },
];

const Sort = ({ value, onClickSort }) => {
  const [showSort, setShowSort] = useState(false);
  const sortRef = useRef();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!event.composedPath().includes(sortRef.current)) {
        setShowSort(false);
        console.log(event)
      }
    };
    document.body.addEventListener("click", handleClickOutside);

    return document.body.addListener("click", handleClickOutside);
  }, []);

  const statusChoosing = (obj) => {
    onClickSort(obj);
    setShowSort((prev) => !prev);
  };

  return (
    <div ref={sortRef} className="sort">
      <div className="sort__label" onClick={() => setShowSort((prev) => !prev)}>
        <svg
          width="10"
          height="6"
          viewBox="0 0 10 6"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M10 5C10 5.16927 9.93815 5.31576 9.81445 5.43945C9.69075 5.56315 9.54427 5.625 9.375 5.625H0.625C0.455729 5.625 0.309245 5.56315 0.185547 5.43945C0.061849 5.31576 0 5.16927 0 5C0 4.83073 0.061849 4.68424 0.185547 4.56055L4.56055 0.185547C4.68424 0.061849 4.83073 0 5 0C5.16927 0 5.31576 0.061849 5.43945 0.185547L9.81445 4.56055C9.93815 4.68424 10 4.83073 10 5Z"
            fill="#2C2C2C"
          />
        </svg>
        <b>Сортировка по:</b>
        <span>{value.name}</span>
      </div>
      {showSort && (
        <div className="sort__popup">
          <ul>
            {sortStatuses.map((obj, index) => (
              <li
                key={index}
                className={obj.sortName === value.sortName ? "active" : ""}
                onClick={() => statusChoosing(obj)}
              >
                {obj.name}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Sort;
