import { useState, useEffect, useContext } from "react";
import { useSelector, useDispatch } from "react-redux";

import PizzaBlock from "../components/PizzaBlock/PizzaBlock";
import Sort from "../components/Sort/Sort";
import Categories from "../components/Categories/Categories";
import Skeleton from "../components/PizzaBlock/Skeleton";
import setCategoriesType from '../redux/slices/filterSlice';
import { SearchContext } from "../App";

const Home = () => {
  const categoriesType = useSelector((state) => state.filter.categoriesType)
  const dispatch = useDispatch()
  const { searchValue } = useContext(SearchContext);
  const [pizzas, setPizzas] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  // const [categoriesType, setCategoriesType] = useState(0);
  const [sortStatus, setSortStatus] = useState({
    name: "популярности",
    sortName: "rating",
  });
  
  const order = `&order${sortStatus.sortName.includes("-") ? "=desc" : "=asc"}`;
  const categories = categoriesType === 0 ? "?" : `?category=${categoriesType}`;
  const sortBy = `&sortBy=${sortStatus.sortName.replace("-", "")}`;

  const onClickCategorie = (i) => {
    dispatch(setCategoriesType(i))
  }

  useEffect(() => {
    setIsLoading(true);
    fetch(
      `https://68efdb57b06cc802829ef4c8.mockapi.io/items${
        categories + sortBy + order
      }`
    )
      .then((res) => res.json())
      .then((arr) => {
        setPizzas(arr);
        setIsLoading(false);
      });
  }, [categories, sortBy, order]);

  const skeleton = Array.from({ length: 6 }).map((_, index) => (
    <Skeleton key={index} />
  ));

  const pizzasBlocks = pizzas
    .filter((item) => {
      if (item.title.toLowerCase().includes(searchValue.toLowerCase())) {
        return true;
      } else {
        return false;
      }
    })
    .map((item) => <PizzaBlock key={item.id} {...item} />);

  return (
    <>
      <div className="container__top">
        <Categories
          value={categoriesType}
          onClickCategorie={(i) => onClickCategorie(i)}
        />
        <Sort value={sortStatus} onClickSort={(i) => setSortStatus(i)} />
      </div>
      <h2 className="content__title">Все пиццы</h2>
      <div className="content__items">
        {isLoading ? skeleton : pizzasBlocks}
      </div>
    </>
  );
};

export default Home;
