import { useState } from "react";
const Categories = () => {
const [isActive, setIsActive] = useState(0)

const handleClick = (index) => {
  setIsActive(index)
}

  let categories = [
    "Все",
    "Мясные",
    "Вегетарианская",
    "Гриль",
    "Острые",
    "Закрытые",
  ];

  return (
    <div className="categories">
      <ul>
        {
          categories.map((item, index) => (<li key={index} className={isActive===index ? "active" : ""} onClick={handleClick}>{item}</li>))
        }
        </ul>
    </div>
  );
};

export default Categories;
