import { useState } from "react";
const Categories = () => {
  const [isActive, setIsActive] = useState(0);

  let categories = [
    "Все",
    "Мясные",
    "Вегетарианская",
    "Гриль",
    "Острые",
    "Закрытые",
  ];

  return (
    <div className="categories">
      <ul>
        {categories.map((item, index) => (
          <li
            key={index}
            className={isActive === index ? "active" : ""}
            onClick={() => setIsActive(index)}
          >
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Categories;
