import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  categoriesType: 0,
  sort: {
    name: "популярности",
    sortName: "rating",
  },
};

export const filterSlice = createSlice({
    name: "filter",
    initialState,
    reducers:{
        setCatergoryType(state, action){
            state.categoriesType = action.payload;
        }
    }
})

export const { setCatergorType } = filterSlice.actions
export default filterSlice.reducer
